@media print{
    @page {
        size : 100mm 45mm;
        margin : 0;
    }
}
*{
    border:none;
    box-sizing :0px;
}
    .print-format{
        padding : 1px !important;
    }

body{
margin : 0;
padding : 0;
    
}

.label.empty-label {
    border: 1px dashed #999;
    background-color: #f5f5f5;
}

.label .empty-placeholder {
    text-align: center;
    font-size: 14px;
    color: #999;
    margin-top: 20mm;
}


  .wrapper {
    display: flex;
    justify-content: space-between;
    width: 100%;
    margin-bottom: 0px !important;
    page-break-inside: avoid;
  }
  .label {
    width: 100mm;
    border: 0px ;
 
    box-sizing: border-box;
    background: #fff;
    font-size:12px !important;
    /*min-height: 190px; */
    /*position: relative !important;*/
    /*left :2mm;*/
    /*top:2mm;*/
    /*top:-1px !important;*/
    /*margin : 0px auto;*/
    height :50mm !important;
  }
  .brand-header {
    text-align: center;
    font-weight: 700;
    font-size: 18px;
   
    text-transform: uppercase;
    margin-top:0px auto !important;
  }
  
  span{
      margin:0 !important;
      padding:0 !important;
      line-height: inherit !important;
  }
  
  .details-block {
    font-size: 13px;
    line-height: 1.25 !important;
    font-weight: 500;
     font-family: system-ui;
    text-align: left;
    margin-bottom: 0px;
  }
  .barcode-container {
    text-align: center;
    width: 90%;
    margin-top: 2px; /* Space adjusted because MRP is removed */
  }
  .barcode-container img {
    width: 86%;
    height: 76px; 
    display: block;
    margin: 0 auto;
  }
  .serial-number {
    font-size: 18px; 
    font-weight: 500;
    margin-top: 2px;
    text-align: center;
    letter-spacing: 1px;
  }
  
  .boldtext{
      /*font-weight:bold;*/
      padding-left:4px;
  }

html:
{% set all_serials = [] %}

{# ---------------- COLLECT SERIALS FROM SERIAL & BATCH BUNDLE ---------------- #}
{% for item in doc.items %}

  {% if item.serial_and_batch_bundle %}

    {% set bundle = frappe.get_doc(
      "Serial and Batch Bundle",
      item.serial_and_batch_bundle
    ) %}

    {% for entry in bundle.entries %}
      {% if entry.serial_no %}
        {% set _ = all_serials.append({
          "serial": entry.serial_no,
          "item_code": item.item_code
        }) %}
      {% endif %}
    {% endfor %}

  {% endif %}

{% endfor %}

{# Now print in pairs of 2 #}
{% for i in range(0, all_serials|length, 2) %}
  <div class="wrapper">

    {# First label #}
    {% if i < all_serials|length %}
      {% set data1 = all_serials[i] %}
      {% set sn1 = data1.serial %}
      {% set item1 = frappe.get_doc("Item", data1.item_code) %}
      <div class="label">
        {# <div class="brand-header">{{ item1.brand or "NONE" }}</div> #}
        <div class="details-block">
          {% set product_name = "" %}

        {% if item1.custom_departments %}
          {% set groups = frappe.get_all(
              "Item Group",
              filters={
                  "name": item1.custom_departments,
                  "custom_is_departmnent": 1
              },
              fields=["item_group_name"],
              limit=1
          ) %}

          {% if groups %}
            {% set product_name = groups[0].item_group_name %}
          {% endif %}
        {% endif %}




           <span class="boldtext"> &nbsp;&nbsp; Mfd & Mkt By:</span> TZU LIFESTYLE PVT. LTD.<br>
        &nbsp;&nbsp;<span style="font-size:12px !important"> 21, South Patel Nagar, New Delhi-110008, India </span><br>
        &nbsp;&nbsp;<span style="font-size:12px !important"> www.ikshita.com </span> <br>
        <span class="boldtext"> &nbsp;&nbsp; Product: </span> <b> {{ product_name }} </b><br>
          {# <span class="boldtext">&nbsp; Item No:</span> {{ item1.item_code }}<br> #}
          <span class="boldtext"> &nbsp;&nbsp; Style:</span> {{ item1.custom_barcode_code }}<br>
          <span class="boldtext"> &nbsp;&nbsp; Size:</span> <b>{{ item1.custom_size or "" }} </b><br>
          <span class="boldtext"> &nbsp;&nbsp; Color:</span> <b> {{ item1.custom_colour_name or "" }} </b> <br>
          <span class="boldtext"> &nbsp;&nbsp; Net Quantity:</span> {% if item1.custom_count_of_pcs %}
          {{ item1.custom_count_of_pcs }}
          {% else %}
              ""
            {% endif %}  
          <br>

          {# <span class="boldtext"> &nbsp; Fabric Top:</span> {{ item1.custom_top_fabrics or "" }}<br> #}
          {# <span class="boldtext">&nbsp; Fabric Bottom:</span> {{ item1.custom_bottom_fabric or "" }}<br> #}
          {# <span class="boldtext">&nbsp; Fabric Dupatta:</span> {{ item1.custom_dupatta_fabric or "" }}<br> #}
          <span class="boldtext">&nbsp;&nbsp; Brand Name:</span> <b> {{ item1.brand or "" }} </b><br>
          <span class="boldtext">&nbsp;&nbsp; Country Of Origin:</span> {{ item1.country_of_origin or "INDIA" }}<br>
           {% set mrp = frappe.db.get_value(
    "Item Price",
    {
      "item_code": item1.item_code,
      "price_list": "MRP"
    },
    "price_list_rate"
  ) %}
          <span class="boldtext" style="font-size:17px !important; 

    left: 1px;">&nbsp;<b> M.R.P: </b> <b> Rs.{{mrp}}0 </b> </span><br>
          <span class="boldtext" style="font-size:10px !important;    
    left: 2px;
"> &nbsp;&nbsp;<b> (Inclusive of all taxes) </b> </span> <br>
          {# <span class="boldtext"> &nbsp; Mfd & Mktd By:</span> TZU LIFESTYLE PVT. LTD.<br> #}
          {# <span class="boldtext">&nbsp; CUSTOMER CARE:</span> info@tzu.co.in #}

          <span style="font-size:12px !important;line-height: normal;
    left: 4px;">&nbsp;&nbsp;&nbsp;For any feedbacks/complaints Please Contact<br> </span>
          <span style="font-size:12px !important;line-height: normal;
    left: 4px;">&nbsp;&nbsp;&nbsp;our customer care Executive : +91-8448388640<br> </span>
          <span style="font-size:12px !important;line-height: normal;
    left: 4px;">&nbsp;&nbsp;&nbsp;or write us at care@tzu.co.in </span>



        </div>
       <div class="barcode-container">

  <img style="image-rendering: pixelated;" src="{{ generate_custom_barcode(sn1) }}" alt="123"/>
</div>







      </div>
    {% else %}
      {# Empty placeholder if no serial #}
      <div class="label">
        <div class="brand-header"></div>
        <div class="details-block" style="text-align: center; padding-top: 15mm;">
          <span style="font-size: 12px;"></span>
        </div>
        <div class="barcode-container"></div>
      </div>
    {% endif %}

    {# Second label #}
    {% if i+1 < all_serials|length %}
      {% set data2 = all_serials[i+1] %}
      {% set sn2 = data2.serial %}
      {% set item2 = frappe.get_doc("Item", data2.item_code) %}
      <div class="label">
       {# <div class="brand-header">{{ item2.brand or "NONE" }}</div> #}
        <div class="details-block">
           {% set product_name = "" %}

        {% if item2.custom_departments %}
          {% set groups = frappe.get_all(
