              filters={
                  "name": item2.custom_departments,
                  "custom_is_departmnent": 1
              },
              fields=["item_group_name"],
              limit=1
          ) %}

          {% if groups %}
            {% set product_name = groups[0].item_group_name %}
          {% endif %}
        {% endif %}

        <span class="boldtext"> &nbsp; Mfd & Mkt By:</span> TZU LIFESTYLE PVT. LTD.<br>
        &nbsp;<span style="font-size:12px !important"> 21, South Patel Nagar, New Delhi-110008, India </span><br>
        &nbsp;<span style="font-size:12px !important"> www.ikshita.com </span> <br>
        <span class="boldtext"> &nbsp; Product:  </span> <b> {{ product_name }} </b><br>
          {# <span class="boldtext">&nbsp; Item No:</span> {{ item2.item_code }}<br> #}
          <span class="boldtext"> &nbsp; Style:</span> {{ item2.custom_barcode_code }}<br>
          <span class="boldtext">&nbsp; Size:</span> <b>{{ item2.custom_size or "" }}</b><br>
          <span class="boldtext">&nbsp; Color:</span> <b> {{ item2.custom_colour_name or "" }}</b><br>
          <span class="boldtext">&nbsp; Net Quantity:</span> {{ item2.custom_count_of_pcs or "" }}<br>
          <span class="boldtext">&nbsp; Brand Name:</span><b> {{ item2.brand or "" }} </b><br>
          <span class="boldtext">&nbsp; Country Of Origin:</span> {{ item2.country_of_origin or "INDIA" }}<br>
                    {% set mrp = frappe.db.get_value(
    "Item Price",
