body{
margin : 0;
padding : 0;
    
}

.label.empty-label {
    border: 1px dashed #999;
    background-color: #f5f5f5;
}

.label .empty-placeholder {
    text-align: center;
    font-size: 14px;
    color: #999;
    margin-top: 20mm;
}


  .wrapper {
    display: flex;
    justify-content: space-between;
    width: 100%;
    margin-bottom: 0px !important;
    page-break-inside: avoid;
  }
  .label {
    width: 100mm;
    border: 0px ;
 
    box-sizing: border-box;
    background: #fff;
    font-size:12px !important;
    /*min-height: 190px; */
    /*position: relative !important;*/
    /*left :2mm;*/
    /*top:2mm;*/
    /*top:-1px !important;*/
    /*margin : 0px auto;*/
    height :50mm !important;
  }
  .brand-header {
    text-align: center;
    font-weight: 700;
    font-size: 18px;
   
    text-transform: uppercase;
    margin-top:0px auto !important;
  }
  
  span{
      margin:0 !important;
      padding:0 !important;
      line-height: inherit !important;
  }
  
  .details-block {
    font-size: 13px;
    line-height: 1.25 !important;
    font-weight: 500;
     font-family: system-ui;
    text-align: left;
    margin-bottom: 0px;
  }
  .barcode-container {
    text-align: center;
    width: 90%;
    margin-top: 2px; /* Space adjusted because MRP is removed */
  }
  .barcode-container img {
    width: 86%;
    height: 76px; 
    display: block;
    margin: 0 auto;
  }
  .serial-number {
    font-size: 18px; 
    font-weight: 500;
    margin-top: 2px;
    text-align: center;
    letter-spacing: 1px;
  }
  
  .boldtext{
      /*font-weight:bold;*/
      padding-left:4px;
  }

html:
{% set all_labels = [] %}

{# -------------------------------------------------
  STEP 1: Collect items & repeat by quantity
------------------------------------------------- #}
{% for row in doc.items %}
  {% set qty = row.qty | int %}
  {% for i in range(qty) %}
    {% set _ = all_labels.append({
      "item_code": row.item_code
    }) %}
  {% endfor %}
{% endfor %}

{# -------------------------------------------------
  STEP 2: Print labels in pairs
------------------------------------------------- #}
{% for i in range(0, all_labels|length, 2) %}
<div class="wrapper">

  {# ================= FIRST LABEL ================= #}
  {% if i < all_labels|length %}
    {% set data1 = all_labels[i] %}
    {% set item1 = frappe.get_doc("Item", data1.item_code) %}

    {# Get Barcode from Item Barcode table #}
    {% set barcode1 = frappe.db.get_value(
      "Item Barcode",
      { "parent": item1.name },
      "barcode"
    ) %}

    <div class="label">
      <div class="details-block">

        {% set product_name = "" %}
        {% if item1.custom_departments %}
          {% set groups = frappe.get_all(
            "Item Group",
            filters={
              "name": item1.custom_departments,
              "custom_is_departmnent": 1
            },
            fields=["item_group_name"],
            limit=1
          ) %}
          {% if groups %}
            {% set product_name = groups[0].item_group_name %}
          {% endif %}
        {% endif %}

        <span class="boldtext">&nbsp;&nbsp; Mfd & Mkt By:</span> TZU LIFESTYLE PVT. LTD.<br>
        &nbsp;&nbsp;<span style="font-size:12px"> 21, South Patel Nagar, New Delhi-110008, India</span><br>
        &nbsp;&nbsp;<span style="font-size:12px"> www.ikshita.com</span><br>

        <span class="boldtext">&nbsp;&nbsp; Product:</span> <b>{{ product_name }}</b><br>
        <span class="boldtext">&nbsp;&nbsp; Style:</span> {{ item1.custom_barcode_code }}<br>
        <span class="boldtext">&nbsp;&nbsp; Size:</span> <b>{{ item1.custom_size or "" }}</b><br>
        <span class="boldtext">&nbsp;&nbsp; Color:</span> <b>{{ item1.custom_colour_name or "" }}</b><br>
         <span class="boldtext"> &nbsp;&nbsp; Net Quantity:</span> {% if item1.custom_count_of_pcs %}
          {{ item1.custom_count_of_pcs }}
          {% else %}
              ""
            {% endif %}  
          <br>
        <span class="boldtext">&nbsp;&nbsp; Brand Name:</span> <b>{{ item1.brand or "" }}</b><br>
        <span class="boldtext">&nbsp;&nbsp; Country Of Origin:</span> {{ item1.country_of_origin or "INDIA" }}<br>

           {% set mrp = frappe.db.get_value(
    "Item Price",
    {
      "item_code": item1.item_code,
      "price_list": "MRP"
    },
    "price_list_rate"
  ) %}
          <span class="boldtext" style="font-size:17px !important;     font-size: 17px !important;
    
    left: 1px;">&nbsp;<b> M.R.P: </b> <b> Rs.{{mrp}}0 </b> </span><br>
          <span class="boldtext" style="font-size:10px !important;    
    left: 2px;
"> &nbsp;&nbsp;<b> (Inclusive of all taxes) </b> </span> <br>

       <span style="font-size:12px !important;line-height: normal;
    left: 4px;">&nbsp;&nbsp;&nbsp;For any feedbacks/complaints Please Contact<br> </span>
          <span style="font-size:12px !important;line-height: normal;
    left: 4px;">&nbsp;&nbsp;&nbsp;our customer care Executive : +91-8448388640<br> </span>
          <span style="font-size:12px !important;line-height: normal;
    left: 4px;">&nbsp;&nbsp;&nbsp;or write us at care@tzu.co.in </span>
      </div>

      <div class="barcode-container">
        {% if barcode1 %}
          <img src="{{ generate_custom_barcode(barcode1) }}" />
        {% endif %}
      </div>
    </div>
  {% endif %}

  {# ================= SECOND LABEL ================= #}
  {% if i + 1 < all_labels|length %}
    {% set data2 = all_labels[i+1] %}
    {% set item2 = frappe.get_doc("Item", data2.item_code) %}

    {% set barcode2 = frappe.db.get_value(
      "Item Barcode",
      { "parent": item2.name },
      "barcode"
    ) %}

    <div class="label">
      <div class="details-block">
        {% set product_name = "" %}

        {% if item2.custom_departments %}
          {% set groups = frappe.get_all(
              "Item Group",
              filters={
                  "name": item2.custom_departments,
                  "custom_is_departmnent": 1
              },
              fields=["item_group_name"],
              limit=1
          ) %}
        
          {% if groups %}
            {% set product_name = groups[0].item_group_name %}
          {% endif %}
        {% endif %}
        <span class="boldtext">&nbsp; Mfd & Mkt By:</span> TZU LIFESTYLE PVT. LTD.<br>
        &nbsp;<span style="font-size:12px"> 21, South Patel Nagar, New Delhi-110008, India</span><br>
        &nbsp;<span style="font-size:12px"> www.ikshita.com</span><br>

        <span class="boldtext">&nbsp; Product:</span> <b>{{ product_name }}</b><br>
        <span class="boldtext">&nbsp; Style:</span> {{ item2.custom_barcode_code }}<br>
        <span class="boldtext">&nbsp; Size:</span> <b>{{ item2.custom_size or "" }}</b><br>
        <span class="boldtext">&nbsp; Color:</span> <b>{{ item2.custom_colour_name or "" }}</b><br>
         <span class="boldtext"> &nbsp;&nbsp; Net Quantity:</span><b> {% if item2.custom_count_of_pcs %}
          {{ item2.custom_count_of_pcs }}
          {% else %}
              ""
            {% endif %}  </b>
          <br>
        <span class="boldtext">&nbsp; Brand Name:</span> <b>{{ item2.brand or "" }}</b><br>
       <span class="boldtext">&nbsp; Country Of Origin:</span> {{ item2.country_of_origin or "INDIA" }}<br>

           {% set mrp = frappe.db.get_value(
    "Item Price",
    {
      "item_code": item2.item_code,
      "price_list": "MRP"
    },
    "price_list_rate"
  ) %}
          <span class="boldtext" style="font-size:17px !important;     font-size: 17px !important;
    
    left: 1px;">&nbsp;<b> M.R.P: </b> <b> Rs.{{mrp}}0 </b> </span><br>
          <span class="boldtext" style="font-size:10px !important;    
    left: 2px;
"> &nbsp;&nbsp;<b> (Inclusive of all taxes) </b> </span> <br>

       <span style="font-size:12px !important;line-height: normal;
    left: 4px;">&nbsp;&nbsp;&nbsp;For any feedbacks/complaints Please Contact<br> </span>
          <span style="font-size:12px !important;line-height: normal;
    left: 4px;">&nbsp;&nbsp;&nbsp;our customer care Executive : +91-8448388640<br> </span>
          <span style="font-size:12px !important;line-height: normal;
    left: 4px;">&nbsp;&nbsp;&nbsp;or write us at care@tzu.co.in </span>

      </div>

      <div class="barcode-container">
        {% if barcode2 %}
          <img src="{{ generate_custom_barcode(barcode2) }}" />
        {% endif %}
      </div>
    </div>
  {% endif %}

</div>

{# ---------------- Page break after 4 rows ---------------- #}
{% if loop.index is divisibleby 4 and not loop.last %}
  <div style="page-break-after: always;"></div>
{% endif %}
{% endfor %}
