css:
@page {
  size: 238px 210px;
  margin: 0;
}

body {
  margin: 0;
  padding: 0;
  font-family: "Arial Narrow", "Helvetica Neue", Arial, sans-serif;
}

html:
{% set it = frappe.get_doc("Item", doc.item_code) %}

<div style="
  width:238px;
  height:210px;
  padding:6px;
  box-sizing:border-box;
  overflow:hidden;
  font-family:'Arial Narrow','Helvetica Neue',Arial,sans-serif;
  color:#000;
">

  <!-- BRAND -->
  <div style="
    text-align:center;
    font-weight:700;
    font-size:15px;
    letter-spacing:0.5px;
    margin-bottom:2px;
  ">
    {{ it.brand or "IKSHITA" }}
  </div>

  <!-- DETAILS -->
  <div style="
    font-size:10px;
    line-height:1.15;
    font-weight:600;
  ">
    Product- {{ it.item_name }}<br>
    Item Code- {{ it.item_code }}<br>
    Deisgn NO- {{ it.item_code }}<br>
    Color- {{ it.get("custom_colour_name","") }}<br>
    Size- {{ it.get("custom_size","") }}<br>
    Fabric Top- {{ it.get("custom_top_fabrics","") }}<br>
    Fabric Bottom- {{ it.get("custom_bottom_fabric","") }}<br>
    Fabric Dupatta- {{ it.get("custom_dupatta_fabric","") }}<br>
    Country of Origin- {{ it.country_of_origin or "INDIA" }}
  </div>

  <hr style="margin:3px 0; border:0;">

  <!-- BARCODE -->
  <div style="text-align:center;">
    <img src="https://barcode.tec-it.com/barcode.ashx?data={{ doc.name }}&code=Code128&translate-esc=true"
         style="width:55mm; height:auto;">
    <p style="margin:2px 0;"><b>{{ doc.name }}</b></p>
  </div>

</div>
